"use strict";

var mysql = require('mysql');
var config = require('../config');
var dbconfig = {
  host: config.mysql.host,
  user: config.mysql.user,
  password: config.mysql.password,
  database: config.mysql.database
};
var conexion;
function conMysql() {
  conexion = mysql.createConnection(dbconfig);
  conexion.connect(function (err) {
    if (err) {
      console.log('db err'.err);
      setTimeout(conMysql, 200);
    } else {
      console.log("base de datos conectada...");
    }
  });
  conexion.on('error', function (err) {
    console.log('db err'.err);
    if (err.code === 'PROTOCOL_CONNECTION_LOST') {
      conMysql();
    } else {
      throw err;
    }
  });
}
conMysql();

// funciones sql

//select
function todos(tabla) {
  return new Promise(function (resolve, reject) {
    conexion.query("SELECT * FROM ".concat(tabla), function (error, result) {
      if (error) return reject(error);
      resolve(result);
    });
  });
}

//select id
function uno(tabla, id) {
  return new Promise(function (resolve, reject) {
    conexion.query("SELECT * FROM ".concat(tabla, " WHERE id=").concat(id), function (error, result) {
      if (error) return reject(error);
      resolve(result);
    });
  });
}

//insert

function agregar(tabla, data) {
  return new Promise(function (resolve, reject) {
    conexion.query("INSERT INTO ".concat(tabla, " SET ?"), data, function (error, result) {
      if (error) return reject(error);
      resolve(result);
    });
  });
}

//update

function actualizar(tabla, id, data) {
  return new Promise(function (resolve, reject) {
    conexion.query("UPDATE ".concat(tabla, " SET ? WHERE id= ?"), [data, id], function (error, result) {
      if (error) return reject(error);
      resolve(result);
    });
  });
}

//delete
function eliminar(tabla, id) {
  return new Promise(function (resolve, reject) {
    conexion.query("DELETE FROM ".concat(tabla, " WHERE id=").concat(id), function (error, result) {
      if (error) return reject(error);
      resolve(result);
    });
  });
}

// login

function query(tabla, consulta) {
  return new Promise(function (resolve, reject) {
